from InventoryInterface import InventoryInterfacefrom Product import Productclass Inventory(InventoryInterface):    def __init__(self):        self.products = {}    def add_product(self, product: Product) -> None:        if product.name in self.products:            print(f"Product '{product.name}' already exists in the inventory.")        self.products[product.name] = product    def remove_product(self, product_name: str) -> None:        if self.get_product(product_name):            del self.products[product_name]    def get_product(self, product_name: str) -> Product:        if product_name in self.products:            return self.products[product_name]        raise ValueError(f"Product '{product_name}' not found in the inventory.")    def get_total_inventory_value(self) -> float:        return sum(product.price for product in self.products.values())